mapper.select:
 
	select 	a.*
	from 	sys10_file a
	where	sys10_file_id = #{fileId}

select * 
from   sys10_file where sys10_parent_id = #{apprId}
union all
select * 
from   sys10_file where sys10_file_id in (
                                         select apr05_file_id 
                                         from   apr05_relate_doc
                                         where  apr05_appr_id = #{apprId}
                                         )

mapper.insert:
insert into sys10_file 
     ( sys10_file_id
     , sys10_parent_id
     , sys10_file_nm
     , sys10_reg_date
     , sys10_server_path
     , sys10_size
     , sys10_note
     , sys10_file_ext ) 
values 
     ( #{fileId}
     , #{parentId}
     , #{fileName}
     , now()
     , #{serverPath}
     , #{size}
     , #{note}
     , #{ext} )

merge into sys10_file a
    using dual
    on
    (
        a.sys10_file_id = #{fileId}
    )    
    when matched then
    update set
        a.sys10_parent_id = #{parentId}
	  , a.sys10_file_nm = #{fileName}
	  , a.sys10_reg_date = sysdate
	  , a.sys10_server_path = #{serverPath}
	  , a.sys10_size = #{size}
	  , a.sys10_note = #{note}
	  , a.sys10_file_ext = #{ext}
	  , a.sys10_title_yn = #{titleYn}
    
    when not matched then
	insert
	(	a.sys10_file_id
	, 	a.sys10_parent_id
	, 	a.sys10_file_nm
	, 	a.sys10_reg_date
	, 	a.sys10_server_path
	, 	a.sys10_size
	, 	a.sys10_note
	,   a.sys10_file_ext
	,   a.sys10_title_yn ) 
	values 
	(	#{fileId}
	,	#{parentId}
	, 	#{fileName}
	, 	sysdate
	, 	#{serverPath}
	, 	#{size}
	, 	#{note}
	,   #{ext}
	,   #{titleYn} )

mapper.update:
update sys10_file
   set sys10_parent_id = #{parentId}
     , sys10_file_nm = #{fileName}
     , sys10_reg_date = now()
     , sys10_server_path = #{serverPath}
     , sys10_size = #{size}
     , sys10_note = #{note}
     , sys10_file_ext = #{ext}
 where sys10_file_id = #{fileId}
